using System;using System.Net;using System.Net.Sockets;namespace Client.TcpClient;public class TcpClient{    private readonly string _ipAddress;    private readonly int _port;    private string _path;    private System.Net.Sockets.TcpClient _client;    private NetworkStream _stream;    public TcpClient(string ip, int port, string path)    {        _ipAddress = ip;        _port = port;        _path = path;        _client = new System.Net.Sockets.TcpClient(_ipAddress, _port);        _stream = _client.GetStream();    }    public string SendMessageToServer()    {        try        {            byte[] data = System.Text.Encoding.UTF8.GetBytes(_path);            _stream.Write(data, 0, data.Length);            data = new byte[1024];            int bytes = _stream.Read(data, 0, data.Length);            string response = System.Text.Encoding.UTF8.GetString(data, 0, bytes);                        return response;        }        catch (Exception e)        {            Console.WriteLine("Невъебическая ноунейм ошибка уровня SSS!!!");        }        return "Не получилось получить данные.";    }    public void CloseConnection()    {        _client.Close();    }}